lines = input.splitlines()

rowsDoubled = []
for i, a in enumerate(lines):
    if '#' not in a:
        rowsDoubled += a,a
    else:
        rowsDoubled += [a]

print(rowsDoubled)

transposed = list(map(list, zip(*rowsDoubled)))

columnsDoubled = []
for i, a in enumerate(transposed):
    if '#' not in a:
        columnsDoubled += a,a
    else:
        columnsDoubled += [a]

withDoubles = list(map(list, zip(*columnsDoubled)))

for i,a in enumerate(withDoubles):
    print(i, a)

galaxies = []
for i,a in enumerate(withDoubles):
    for j,b in enumerate(a):
        if b == '#':
            galaxies += [[i,j]]

print(galaxies)

answer = 0
for a in galaxies:
    for b in galaxies: 
        if b != a:
            answer += abs(b[0]-a[0])+abs(b[1]-a[1])

print(answer/2)


